import React from 'react';
import Joi from 'joi';
import logo from './logo.svg';
import L from 'leaflet';
import './App.css';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import { Card, Button, CardTitle, CardText, Form, FormGroup, Label, Input } from 'reactstrap';


let myIcon = L.icon({
    iconUrl: 'data:image/png;base64,',
    iconSize: [25, 41],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41],

});

const schema = Joi.object().keys({
  name: Joi.string().regex(/^[A-zÀ-ú -_]{1,70}$/).min(2).max(30).required(),
  message: Joi.string().min(1).max(300).required()
});

const API_URL = window.location.hostname == 'localhost' ? 'http://localhost:5000/api/v1/messages' : 'production-url-her';
//-----------------------APP----------------------------------//
class App extends React.Component {
  state = {
    location: {
      lat: 51.505,
      lng: -0.09,
    },
    haveUserLocation: false,
    zoom: 3,
    userMessage: {
      name: '',
      message: ''
    }
  }

  componentDidMount(){
    navigator.geolocation.getCurrentPosition((position)=> {
                 this.setState({
                   location: {
                     lat: position.coords.latitude,
                     lng: position.coords.longitude
                   },
                   haveUserLocation: true,
                   zoom: 13
                 });
            },() =>{
              console.log('No Location given');
              fetch('https://ipapi.co/json')
              //turning response into json
              .then(res => res.json())
              .then(location => {
                this.setState({
                  location: {
                    lat: location.latitude,
                    lng: location.longitude
                  },
                  haveUserLocation: true,
                  zoom: 13
                });
              });
            });
  }
  formIsValid = () =>{
    const userMessage = 
    {
      name: this.state.userMessage.name,
      message: this.state.userMessage.message
    };
    const result = Joi.validate(userMessage, schema);

    return result.error && this.state.haveUserLocation ? false : true;
  }
  formSubmitted = event =>{
    event.preventDefault();
    formIsValid();
    if(!result.error){
      fetch(API_URL, {
        method: 'POST',
        headers: {
          'content-type' : 'application/json'
        },
        body: JSON.stringify({
          ...userMessage,
          latitude : this.state.location.lat,
          longitude: this.state.location.lng
        })
      });
    }
  };

  valueChanged = (event) => {
    const {name, value} = event.target;
    this.setState((previousState) => ({
      userMessage: {
        ...previousState.userMessage,
        [name]: value
      }
    })); 
  };

  render(){
  const position = [this.state.location.lat, this.state.location.lng];

  return (
    <div className="map">
    <Map className="map" center={position} zoom={this.state.zoom}>
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {
          this.state.haveUserLocation ?
            <Marker position={position}
                      icon={myIcon}>
                                <Popup>
                                    A pretty CSS3 popup. <br /> DU her her
                                 </Popup>
            </Marker> : ''
        }
      </Map>
      <Card body className="message-form">
        <CardTitle>Finn hverandre!</CardTitle>
        <CardText>Skriv en melding med din posisjon</CardText>
        <Form onSubmit={this.formSubmitted} >
        <FormGroup>
          <Label for="name">Navn</Label>
          <Input
            onChange={this.valueChanged}
            type="text"
            name="name"
            id="name"
            placeholder="Skriv inn ditt navn" />
        </FormGroup>
        <FormGroup>
          <Label for="message">Melding</Label>
          <Input
            onChange={this.valueChanged}
            type="textarea"
            name="message"
            id="message"
            placeholder="Din melding" />
        </FormGroup>
        <Button type="submit" color="info" disabled={!this.state.haveUserLocation}>Send</Button>
      </Form>
      </Card>
      </div>
        );
  }
}

export default App;
